using System.Collections;using System.Collections.Generic;using UnityEngine;using System;namespace UAR{    public class Logger    {        public enum Type        {            Info,            Warning,            Error        }        public static bool enabled = true;        public static void log(Logger.Type type, string format, params object[] p)        {            if (enabled)            {                format = "[UAR]: " + format;                switch (type)                {                    case Type.Info:                        {                            UnityEngine.Debug.LogFormat(format, p);                            break;                        }                    case Type.Warning:                        {                            UnityEngine.Debug.LogWarningFormat(format, p);                            break;                        }                    case Type.Error:                        {                            UnityEngine.Debug.LogErrorFormat(format, p);                            break;                        }                }                            }        }    }    public class WAnchor    {        public WAnchor(string id, Pose pose) { this.id = id; this.pose = pose; }        public readonly string id;        public  readonly Pose pose;    }    public class IAnchor : WAnchor    {        public IAnchor(string id, Pose pose, bool tracking, string imgName)            : base(id, pose)        { this.tracking = tracking; this.imgName = imgName; }        public readonly bool tracking;        public readonly string imgName;    }    public abstract class UAR : MonoBehaviour    {        protected static readonly HashSet<string> anchors = new HashSet<string>();        public static bool tracking = true;        public static Action<IAnchor> IAnchorAdded;        public static Action<IAnchor> IAnchorUpdated;        public static Action<IAnchor> IAnchorRemoved;        public static Action<WAnchor> WAnchorAdded;        public static Action<WAnchor> WAnchorUpdated;        public static Action<WAnchor> WAnchorRemoved;    }}